import React, { useState, useEffect } from 'react';
import './ShowTransactions.css'

export let ShowTransactions = (props) => {

    let handleChange=(hawker)=>{

        let _hawker=hawker;
        console.log(_hawker,inputVal)
        props.offer(_hawker,parseInt(inputVal))
    }

    let [inputVal, setInputVal] = useState("")

    let inputChangeHandler = (event) => {
        setInputVal(event.target.value)
    }

    let hawkerList=props.hawkers.map((hawker,i)=>
        <tr key={i}>
            <td >{hawker.name}</td>
            <td><input onChange={inputChangeHandler} value={inputVal}></input></td>
            <td>{hawker.amount}</td>
            <td> <button  onClick={handleChange.bind(this,hawker.name)}> Offer </button></td>
        </tr>)

        return(
            <div>
            <h3> Hawkers</h3>
            <hr />
            <table >
                <tbody>
                    <tr>
                        <th>Hawkers</th>
                        <th>Amount</th> 
                    </tr>
                    {hawkerList}
                </tbody>
            </table>
          </div>
        )
    } 


export default ShowTransactions;
